        onDeleteTodoItem(itemid) {
            var self = this
            //删除item，其id为itemid

            var url = 'http://123.57.45.31:9000/todo/v1/todo/' + itemid
            //   url = 'http://127.0.0.1:9000/todo/v1/todo/' + itemid

            //使用delete方法删除数据,注意传参方式
            axios
